record.field:
# Create a payment voucher from selected vendor bills
active_ids = env.context.get('active_ids')
active_model = env.context.get('active_model')

if active_model == 'account.move' and active_ids:
    moves = env[active_model].browse(active_ids)

    # Filter to only valid bills (posted, vendor invoices or refunds)
    valid_moves = moves.filtered(
        lambda m: m.state == 'posted' and m.move_type in ['in_invoice', 'in_refund']
    )

    if not valid_moves:
        raise UserError("No valid bills selected. Please select posted vendor invoices or refunds.")

    # Create a payment voucher
    voucher = env['account.payment.voucher'].create({
        'line_ids': []
    })

    # Add each selected bill as a line in the voucher
    for move in valid_moves:
        voucher.write({
            'line_ids': [(0, 0, {
                'partner_id': move.partner_id.id,
                'move_id': move.id,
                'amount_to_pay_gross': abs(move.amount_residual_signed),  # Use absolute value for refunds
                'wht_base_amount': abs(move.amount_residual_signed),  # Set WHT base to gross amount by default
            })]
        })

    # Open the created payment voucher
    action = {
        'type': 'ir.actions.act_window',
        'res_model': 'account.payment.voucher',
        'view_mode': 'form',
        'res_id': voucher.id,
        'target': 'current'
    }
